queries.code:
query StaffingTemplateSelect_Query {Template {
        StaffingTemplates(first: 20, filterByName: $filterByName, alwaysIncludeIds: $alwaysIncludeIds) {
            edges {
                node {
                    ...StaffingTemplateSelect_StaffingTemplateFragment
                }
            }
        }
    }}

mutations.code:
mutation EditStaffingTemplateModal_CreateMutation {Template {
        createStaffingTemplate(input: $input) {
            edge @appendEdge(connections: $connections) {
                node {
                    id
                    ...EditStaffingTemplateButton_StaffingTemplateFragment
                }
            }
        }
    }}

mutation EditStaffingTemplateModal_EditMutation {Template {
        editStaffingTemplate(input: $input) {
            edge {
                node {
                    id
                    ...EditStaffingTemplateButton_StaffingTemplateFragment
                }
            }
        }
    }}

mutation ImportStaffingTemplatesButton_ImportMutation {Template {
        importStaffingTemplates(input: $input) {
            result {
                editedEntities
                newEntities
                issues {
                    row
                    issue
                }
            }
        }
    }}

fragments.code:
fragment EditStaffingTemplateModal_StaffingTemplateFragment on ... {id
    name
    assignmentRoleAssociations {
        assignmentRoleRef
        isExecutive
    }}
